    .datatable .lt,
    .datatable .rt,
    .datatable .lb,
    .datatable .rb,
    .datatable .ilt,
    .datatable .irt {
        display: none;
    }

    .datatable table {
        margin-top: 10px;
        border-style: solid;
        border-color: #e4e6eb;
        border-width: 1px;
    }

